import './App.css';
import React,{ Component } from 'react';
import {data} from './const/data'

class SearchScrollBox extends Component {
// constructor(props) {
//     super(props)
// }
filterFun = () => {
    const filteredData = data.filter((el) => {
        //if no input the return the original
        if (this.props.input === '') {
            return el;
        }
        //return the item which contains the user input
        else {
            switch (this.props.input) {
                case el.name.toLowerCase().includes(this.props.input):
                    return el.name.toLowerCase().includes(this.props.input);
                case el.address.toLowerCase().includes(this.props.input):
                    return el.address.toLowerCase().includes(this.props.input);

                default:
                    break;
            }
            return el.name.toLowerCase().includes(this.props.input)
        }
    })
    return (
        <ul className="ul-list">
            {filteredData.map((item) => (
                <li key={item.id} className="items">
                    <p>{item.name}</p>
                    <p>{item.items}</p>
                    <p>{item.address}, {item.pincode}</p>
                </li>
            ))}
        </ul>
    )
    }
render() {

    return (
        <div className='search-scroll-view'>
            {this.filterFun()}
        </div>
    )
}
}


export default SearchScrollBox;
